mapper.select:
select
# 生成序列号
row_number() over (order by website_link.id) as id,
website_link.url,
website_link.ext_link as extLink,
website_link.url_type as urlType,
website_link.created_at,
website_info.name as websiteName,
website_link.updated_at
from website_link left join website_info on website_link.website_id=website_info.id
where website_id = #{websiteId}

    and url like concat('%', #{url}, '%')


    and ext_link = #{extLink}


    and url_type = #{urlType}


    and website_link.created_at >= #{startTime}


    and website_link.created_at <= #{endTime}
